    private void btAcao1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btAcao1ActionPerformed
        
        // Gerando tabuada
        Integer tabuada = Integer.valueOf(tfCampo1.getText());
        
        for (Integer contador = 0; contador <= 10; contador++) {
            String texto = String.format("%d x %d = %d \n", tabuada, contador, (tabuada * contador));
            taResultado.setText(taResultado.getText() + texto);
        }
        
        
    }//GEN-LAST:event_btAcao1ActionPerformed

